import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

function SearchBar(props) {
  const [countries, setCountries] = useState({
    countriesList: [],
    name: "",
  });

  function handleChange(e) {
    const { value } = e.target;
    setCountries(value);
  }

  function filterCountries(term) {
    const filteredArr = countries.filter((country) => {
      return country.name.includes(term);
    });
    setCountries(filteredArr);
    console.log();
  }

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(
          `https://travelbriefing.org/${countries}?format=json`
        );
        setCountries({ countriesList: [...response.data] });
      } catch (err) {
        console.error(err);
      }
    }

    fetchData();
  }, []);

  return (
    <div>
      <input
      className="Search"
        type="text"
        name="search"
        placeholder="Which country do you want to visit?"
        // value={search}
        onChange={handleChange}
      />
      {countries.map((country) => {
        <Link to={`/${countries.name}?format=json`}>
          <button className="bnt-r">Search</button>
        </Link>;
      })}
    </div>
  );
}

export default SearchBar;
