        async function fetchData() {
            const { name } = props.match.params
            const reponse = await Axios.get(`https://travelbriefing.org/${name}?format=json`);

            setContries({reponse})

            console.log(reponse)
        }

        fetchData()


    }, [contries])
    console.log(props)
    return (
        <>  
            <div>{contries.reponse.data.language[0].language}</div>
            <div>{contries.reponse.data.currency.name}</div>
            <div>{contries.reponse.data.timezone.name}</div>
        </>
    )
}

export default Contriesdetails;
